
function rowFilter(activeText, onclickText, justText) {
    return '<li class="list-group-item '.concat(activeText).concat('" onclick="').concat(onclickText).concat('">').concat(justText).concat('</li>');
}

function rowExercise(activeText, justText) {
    return '<li class="list-group-item '.concat(activeText).concat('">').concat(justText).concat('</li>');
}

function rowDay(justText, num) {
    button='';
    if (num!='') button='<button type="button" class="btn btn-secondary">Rezervi≈°i</button>';
    return  '<li class="list-group-item d-flex justify-content-between align-items-center">'.
        concat(justText).concat('<span class="badge badge-secondary badge-pill">').concat(num).concat('</span>').
        concat(button).concat('</li>'); 
}

function filterYoga() {
    newInnerHTML=rowFilter("","filterAll()","Sve").
        concat(rowFilter("active","filterYoga()","Yoga")).
        concat(rowFilter("","filterPilates()","Pilates")).
        concat(rowFilter("","filterCore()","Core")).
        concat(rowFilter("","filterCardio()","Kardio"));
    document.getElementById('listFilter').innerHTML=newInnerHTML;

    newInnerHTML=rowExercise("active","Yoga 1").
        concat(rowExercise("","Yoga 2")).
        concat(rowExercise("","Yoga 3"));
    document.getElementById('listExercise').innerHTML=newInnerHTML;

    newInnerHTML=rowDay("Nedelja 13h", "20").
        concat(rowDay("Sreda 18h", "")).
        concat(rowDay("Petak 19h", "5"));
    document.getElementById('listDay').innerHTML=newInnerHTML;
}


